mapper.insert:
INSERT	INTO	QNA (
						QNA_ID	
						, WRITER
						, PASSWORD
						, CONTENT
						, IS_VISIBLE
						, CRTDT
						, SUBJECT
					)
VALUES				(
						'QA-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(QNA_ID_SEQ.NEXTVAL, 6, '0')	
						, #{writer}
						, #{password}
						, #{content}
						, #{isVisible}
						, SYSDATE
						, #{subject}
					)

mapper.select:
SELECT * FROM ( SELECT A.* , ROWNUM RNUM FROM ( SELECT QNA_ID , WRITER , PASSWORD , CONTENT , IS_VISIBLE , CRTDT , SUBJECT FROM QNA
WHERE SUBJECT LIKE '%' || #{searchKeyword} || '%'
ORDER BY QNA_ID DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE RNUM >= #{startRow}
