mapper.insert:
INSERT	INTO	QNA (
						QNA_ID	
						, WRITER
						, PASSWORD
						, CONTENT
						, IS_VISIBLE
						, CRTDT
						, SUBJECT
					)
VALUES				(
						'QA-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(QNA_ID_SEQ.NEXTVAL, 6, '0')	
						, #{writer}
						, #{password}
						, #{content}
						, #{isVisible}
						, SYSDATE
						, #{subject}
					)
