mapper.insert:
INSERT	INTO	SHOWINGLIST
								(
									SHOWING_ID
									, MOVIE_CODE
									, SHOWING_NUM
									, START_TIME
									, END_TIME
								)
VALUES	(
			'SL-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(SHO_ID_SEQ.NEXTVAL, 6, '0')
			, #{movieCode}
			, #{showingNum}
			, #{startDate}
			, #{endDate}
		)

mapper.select:
SELECT S.SHOWING_ID , S.MOVIE_CODE S_MOVIE_CODE , S.SHOWING_NUM , S.START_TIME , S.END_TIME , M.MOVIE_CODE M_MOVIE_CODE , M.MOVIE_NAME , M.RUNNING_TIME FROM SHOWINGLIST S , MOVIE M WHERE S.MOVIE_CODE = M.MOVIE_CODE
AND S.MOVIE_CODE = #{_parameter}
ORDER BY S.START_TIME DESC

SELECT	COUNT(1)
FROM	SHOWINGLIST
WHERE	START_TIME >= #{startDate}
AND		END_TIME <= #{endDate}
AND		SHOWING_NUM = #{showingNum}
