mapper.insert:
INSERT	INTO	MEMBERS (
							ID
							, PASSWORD
							, EMAIL
							, PHONE
							, MILEAGE
							, SALT
							, LOGIN_FAIL_COUNT
							, LATEST_LOGIN
							, IS_ADMIN
						)
VALUES					(
							#{id}
							, #{password}
							, #{email}
							, #{phone}
							, 0
							, #{salt}
							, 0
							, SYSDATE
							, 'N'
						)

mapper.select:
SELECT	ID
FROM	MEMBERS
WHERE	ID = #{id}
