mapper.insert:
INSERT	INTO	MEMBERS (
							ID
							, PASSWORD
							, EMAIL
							, PHONE
							, MILEAGE
							, SALT
							, LOGIN_FAIL_COUNT
							, LATEST_LOGIN
							, IS_ADMIN
							, NAME
						)
VALUES					(
							#{id}
							, #{password}
							, #{email}
							, #{phone}
							, 0
							, #{salt}
							, 0
							, SYSDATE
							, 'N'
							, #{name}
						)

mapper.select:
SELECT	ID
FROM	MEMBERS
WHERE	ID = #{id}

SELECT	SALT
FROM	MEMBERS
WHERE	ID = #{id}

mapper.update:
UPDATE	MEMBERS
SET		LOGIN_FAIL_COUNT = 0
WHERE	ID = #{id}

mapper.select:
SELECT	ID
FROM	MEMBERS
WHERE	NAME = #{name}
AND		EMAIL = #{email}
AND		PHONE = #{phone}
