mapper.insert:
INSERT	INTO	NOTICE	(
							NOTICE_ID
							, SUBJECT
							, CONTENT
							, ADMIN_ID
							, CRTDT
						)
VALUES					(
							'NO-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(NOT_ID_SEQ.NEXTVAL, 6, '0')
							, #{subject}
							, #{content}
							, #{adminId}
							, SYSDATE
						)
